#for using csv.reader(), import csv
import csv
#for using the random number generator, import random
import random 

#function to create dictionary of date as key and winning lottery number combination
#as value and to return a dictionary containing the number and its frequency in the lottery sequence
def dict_creator(a_file):

    reader=csv.reader(a_file)
    numberdict={}
    count=0
    dict_1={}
    for j in range(1,55):
        numberdict[j]=0
    for row in reader:
        valueset=set()
        date_str=str(row[1]+'/'+row[2]+'/'+row[3])
        valueset.add(int(row[4]))
        numberdict[int(row[4])]= numberdict[int(row[4])]+1
        valueset.add(int(row[5]))
        numberdict[int(row[5])]= numberdict[int(row[5])]+1
        valueset.add(int(row[6]))
        numberdict[int(row[6])]= numberdict[int(row[6])]+1
        valueset.add(int(row[7]))
        numberdict[int(row[7])]= numberdict[int(row[7])]+1
        valueset.add(int(row[8]))
        numberdict[int(row[8])]= numberdict[int(row[8])]+1
        valueset.add(int(row[9]))
        numberdict[int(row[9])]= numberdict[int(row[9])]+1
        dict_1[date_str]=valueset
        count=count+1
    return dict_1, count, numberdict
        
#function to generate a random number
def randomize():
    aset = set()

    for i in range(0,6):

       aset.add(random.randint(1,54))
    print("customer your ticket number is: ", aset)
    return aset

#function to calculate the amount won and return a
#list of dates with the amount won on that day
def won(dict_2,a_set):
    prize=0
    challenge1={}
    int_set=set()
    for b_set in dict_2.values():
        int_set=b_set.intersection(a_set)
        p=len(int_set)
        l=int(p)
        if(l==2):
            prize=prize+2
        elif(l==3):
            prize=prize+10       
        elif(l==4):
            prize=prize+100
        elif(l==5):
            prize=prize+10000
        elif(l==6):
            prize=prize+100000
        if l==2 or l==3 or l==4 or l==5 or l==6:
            for key,value in dict_2.items():
                if(value==b_set):
                    key1=key
                if(l==2):
                   prize1=2
                elif(l==3):
                   prize1=10
                elif(l==4):
                   prize1=100
                elif(l==5):
                   prize1=10000
                elif(l==6):
                   prize1=100000   
            challenge1[key1]=prize1
    return prize, challenge1

#main program

#introduction of the program
print("WELCOME TO THE LOTTERY SYSTEM")
print("-"*30)

#opening the csv file and creating a dictionary by calling the dict_creator
#function with the file object name
a1_file= open("lottotexas.csv","r")
dict1,count1,challenge2 = dict_creator(a1_file)

#calling random function to generate a ticket for the customer
ticket = randomize()

#calling the won function with the dictionary provided by the dict_creator function and
#random ticket number generated buy the random() function as the arguments to calculate the amount won
amount,challenge1=won(dict1,ticket)

#displaying the result
print("the dates with the amount won on that date list is : ", challenge1)
print("the frequency of numbers from 1 to 54 through dictionary is : ", challenge2)
print("the total money your ticket won in 25 years is: $", amount)
